mapper.select:
SELECT A.* FROM( SELECT asset_seq ,asset_number ,kubun_code ,purchase_date ,warranty_period ,buy_to ,purchase_price ,status_code ,maker_name ,model_name ,serial_num ,interface_col ,os_name ,os_product_key ,cpu_processor ,operating_frequency ,memory ,memory_upgarde ,hdd ,hdd_upgarde ,mac_lan ,mac_wireless ,other ,security_bios ,security_hdd ,admin_account_id ,admin_account_pwd ,storage_location FROM asset ORDER BY asset_seq ASC ) A WHERE 1 = 1
AND asset_seq = #{assetSeq}
AND asset_number like '%${assetNumber}%'
AND kubun_code = #{kubunCode}
AND status_code = #{statusCode}
AND maker_name like '%${makerName}%'
AND model_name like '%${modelName}%'
AND purchase_date BETWEEN #{startPurchaseDate} and #{endPurchaseDate}
LIMIT #{length} OFFSET #{start}

    SELECT COUNT(1)
    FROM asset
	WHERE 1 = 1

		AND asset_seq = #{assetSeq}


		AND asset_number like '%${assetNumber}%'


		AND kubun_code = #{kubunCode}


		AND status_code = #{statusCode}


		AND maker_name like '%${makerName}%'


		AND model_name like '%${modelName}%'


		AND purchase_date BETWEEN #{startPurchaseDate} and #{endPurchaseDate}

mapper.update:
UPDATE asset SET
	kubun_code = #{kubunCode}
	,purchase_date = #{purchaseDate}
	,warranty_period = #{warrantyPeriod}
	,buy_to = #{buyTo}
	,purchase_price = #{purchasePrice}
	,status_code = #{statusCode}
	,maker_name = #{makerName}
	,model_name = #{modelName}
	,serial_num = #{serialNum}
	,interface_col = #{interFaceCol}
	,os_name = #{osName}
	,os_product_key = #{osProductKey}
	,cpu_processor = #{cpuProcessor}
	,operating_frequency = #{operatingFrequency}
	,memory = #{memory}
	,memory_upgarde = #{memoryUpgarde}
	,hdd = #{hdd}
	,hdd_upgarde = #{hddUpgarde}
	,mac_lan = #{macLan}
	,mac_wireless = #{macWireless}
	,other = #{other}
	,security_bios = #{securityBios}
	,security_hdd = #{securityHdd}
	,admin_account_id = #{adminAccountId}
	,admin_account_pwd = #{adminAccountPwd}
	,storage_location = #{storageLocation}
	,update_id = #{updateId}
WHERE asset_seq = #{assetSeq}

UPDATE accessories SET
	item_name = #{itemName},
	item_spec = #{itemSpec},
	item_quantity = #{itemQuantity},
	remarks = #{remarks},
	update_id = #{updateId}
WHERE accessories_seq = #{accessoriesSeq}
AND asset_seq = #{assetSeq}

UPDATE maintenance_history SET
	implementation_date = #{implementationDate},
	implementation_detail = #{implementationDetail},
	rep_id = #{repId},
	approver_id = #{approverId},
	update_id = #{updateId}
WHERE history_seq = #{historySeq}
AND asset_seq = #{assetSeq}
