mapper.select:
SELECT A.* FROM( SELECT code_master_id ,code_detail_id ,code_detail_name ,item1 ,item2 ,item3 ,item4 ,item5 ,use_flag FROM code_detail ORDER BY code_master_id ASC )A where 1=1
AND code_master_id like '%${codeMasterId}%'
AND code_detail_id like '%${codeDetailId}%'
AND code_detail_name like '%${codeDetailName}%'
AND use_flag =#{useFlag}
LIMIT #{length} OFFSET #{start}

mapper.update:
update code_detail set
code_master_id=#{codeMasterId}
,code_detail_id=#{codeDetailId}
,code_detail_name=#{codeDetailName}
,item1=#{item1}
,item2=#{item2}
,item3=#{item3}
,item4=#{item4}
,item5=#{item5}
,use_flag=#{useFlag}
where
code_master_id=#{codeMasterIdOld} and
code_detail_id=#{codeDetailIdOld}

update code_detail set
use_flag=#{useFlag}
WHERE code_detail_id = #{codeDetailId} and code_Master_Id=#{codeMasterId}
