mapper.select:
SELECT * FROM( SELECT asset_number, maker_name, model_name, status_code, 999999999999 rental_no, null purpose, null storage_location, null applicant_id, null rental_user_id, null bp_name, null rentalDayS, null returnDayS, null rental_seq FROM asset WHERE status_code in('01','03','04')
AND status_code = #{statusCode}
UNION SELECT ast.asset_number, ast.maker_name, ast.model_name, IFNULL(rt.status_code,ast.status_code) AS status_code, rt.rental_no, rt.purpose, rt.storage_location, rt.applicant_id, rt.rental_user_id, rt.bp_name, rt.rental_day rentalDayS, rt.return_day returnDayS, rt.rental_seq FROM asset ast JOIN rental rt ON ast.asset_seq = rt.asset_seq WHERE 1 = 1
AND ast.asset_number LIKE '%${assetNumber}%'
AND rt.applicant_id LIKE '%${applicantId}%'
AND rt.status_code = #{statusCode}
AND rt.rental_day BETWEEN #{rentalDayS} AND #{rentalDayE}
AND rt.return_day BETWEEN #{returnDayS} AND #{returnDayE}
)Result ORDER BY asset_number,rental_no LIMIT #{length} OFFSET #{start}

SELECT COUNT(1) FROM( SELECT asset_number, maker_name, model_name, status_code, 999999999999 rental_no, null purpose, null storage_location, null applicant_id, null rental_user_id, null bp_name, null rentalDayS, null returnDayS, null rental_seq FROM asset WHERE status_code in('01','03','04')
AND status_code = #{statusCode}
UNION SELECT ast.asset_number, ast.maker_name, ast.model_name, IFNULL(rt.status_code,ast.status_code) AS status_code, rt.rental_no, rt.purpose, rt.storage_location, rt.applicant_id, rt.rental_user_id, rt.bp_name, rt.rental_day rentalDayS, rt.return_day returnDayS, rt.rental_seq FROM asset ast JOIN rental rt ON ast.asset_seq = rt.asset_seq WHERE 1 = 1
AND ast.asset_number LIKE '%${assetNumber}%'
AND rt.applicant_id LIKE '%${applicantId}%'
AND rt.status_code = #{statusCode}
AND rt.rental_day BETWEEN #{rentalDayS} AND #{rentalDayE}
AND rt.return_day BETWEEN #{returnDayS} AND #{returnDayE}
)Result
