mapper.select:
SELECT ast.asset_number, ast.maker_name, ast.model_name, rt.status_code, rt.rental_no, rt.purpose, rt.storage_location, rt.applicant_id, rt.rental_user_id, rt.bp_name, rt.rental_day rentalDayS, rt.return_day returnDayS, rt.rental_seq FROM asset ast, rental rt WHERE rt.asset_seq = ast.asset_seq
AND ast.asset_number LIKE '%${assetNumber}%'
AND rt.applicant_id LIKE '%${applicantId}%'
AND rt.status_code = #{statusCode}
AND rt.rental_day BETWEEN #{rentalDayS} AND #{rentalDayE}
AND rt.return_day BETWEEN #{returnDayS} AND #{returnDayE}
ORDER BY ast.asset_number LIMIT #{length} OFFSET #{start}

    	SELECT
    		COUNT(1)
    	FROM asset ast, rental rt
		WHERE
		rt.asset_seq = ast.asset_seq

		AND ast.asset_number LIKE '%${assetNumber}%'


		AND rt.applicant_id LIKE '%${applicantId}%'


		AND rt.status_code = #{statusCode}


		AND rt.rental_day BETWEEN #{rentalDayS} AND #{rentalDayE}


		AND rt.return_day BETWEEN #{returnDayS} AND #{returnDayE}
